radio', 'requesttype', '', get_string('evaluationTypes','local_pfc'), 'evaluation_types');        $mform->addGroup($requestTypes, 'requestTypes', '', array(' '), false);        $mform->setDefault('requesttype', 'all');        $mform->addElement('submit', 'submitbutton', get_string('check', 'local_pfc'));    }}/** * * */function local_pfc_make_api_request_to_html($type = '') {    if($type == 'calendars'){        return local_pfc_get_calendars_to_hmtl();    }elseif($type == 'evaluations'){        return local_pfc_get_evaluations_to_hmtl();    }elseif($type == 'evaluation_types'){        return local_pfc_get_evaluation_types_to_hmtl();    }else{        $result = array();        $result[] = local_pfc_get_calendars_to_hmtl();        $result[] = local_pfc_get_evaluations_to_hmtl();        $result[] = local_pfc_get_evaluation_types_to_hmtl();        return implode('<br/>',$result);    }}/** * * */function local_pfc_get_calendars_to_hmtl() {    $result = get_string('calendars','local_pfc').':<br/>';    try{        $json = '['.implode(',',local_pfc_get_calendars()).']';        return $result.'<pre>'.json_encode(json_decode($json), JSON_PRETTY_PRINT).'</pre>';    }catch (\local_pfc\api_exception $e){        return $result.'<pre style="color:#f00;">'.$e->getOriginalMessage().'</pre>';    }}/** * * */function local_pfc_get_calendars() {    $api = new \local_pfc\api\calendar_api();    return $api->get_calendars();}/** * * */function local_pfc_get_evaluations_to_hmtl() {    $result = get_string('evaluations','local_pfc').':<br/>';    try{        $json = '['.implode(',',local_pfc_get_evaluations()).']';        return $result.'<pre>'.json_encode(json_decode($json), JSON_PRETTY_PRINT).'</pre>';    }catch (\local_pfc\api_exception $e){        return $result.'<pre style="color:#f00;">'.$e->getOriginalMessage().'</pre>';    }}/** * * */function local_pfc_get_evaluations() {    $api = new \local_pfc\api\evaluation_api();    return $api->get_evaluations();}/** * * */function local_pfc_get_evaluation_types_to_hmtl() {    try{        $result = get_string('evaluationTypes','local_pfc').':<br/>';        $json = '['.implode(',',local_pfc_get_evaluation_types()).']';        return $result.'<pre>'.json_encode(json_decode($json), JSON_PRETTY_PRINT).'</pre>';    }catch (\local_pfc\api_exception $e){        return $result.'<pre style="color:#f00;">'.$e->getOriginalMessage().'</pre>';    }}/** * * */function local_pfc_get_evaluation_types() {    $api = new \local_pfc\api\evaluation_type_api();    return $api->get_evaluation_types();}